mapper.select:
SELECT p.* FROM ( SELECT pc.problem_cate_id, COUNT(*) AS wrong_count FROM solv s JOIN problem p ON s.problem_id = p.problem_id JOIN problem_cate pc ON p.problem_cate_id = pc.problem_cate_id WHERE s.member_id = #{memberId} AND s.solv_right = 0 GROUP BY pc.problem_cate_id ORDER BY wrong_count DESC LIMIT 1 ) AS t JOIN problem p ON t.problem_cate_id = p.problem_cate_id LEFT JOIN problem_like pl ON p.problem_id = pl.problem_id
WHERE p.problem_id NOT IN ( SELECT problem_id FROM solv WHERE member_id = #{memberId} )
GROUP BY p.problem_id ORDER BY COUNT(pl.problem_id) DESC LIMIT #{limit};
